// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Events {
  AnotherContract anotherContract;

  struct Struct { uint u; uint v; }

  event WithoutArgs();
  event WithUintArg(uint u);
  event WithIntArg(int i);
  event WithTwoUintArgs(uint u, uint v);
  event WithStringArg(string s);
  event WithTwoStringArgs(string s, string t);
  event WithIndexedStringArg(string indexed s);
  event WithBytesArg(bytes b);
  event WithIndexedBytesArg(bytes indexed b);
  event WithBytes32Arg(bytes32 b);
  event WithStructArg(Struct s);
  event WithIndexedBytes32Arg(bytes32 indexed b);
  event WithUintArray(uint[2] a);
  event WithBytes32Array(bytes32[2] a);

  constructor (AnotherContract c) {
    anotherContract = c;
  }

  function doNotEmit() public {}

  function emitWithoutArgs() public {
    emit WithoutArgs();
  }

  function emitUint(uint u) public {
    emit WithUintArg(u);
  }

  function emitInt(int i) public {
    emit WithIntArg(i);
  }

  function emitUintTwice(uint u, uint v) public {
    emit WithUintArg(u);
    emit WithUintArg(v);
  }

  function emitTwoUints(uint u, uint v) public {
    emit WithTwoUintArgs(u, v);
  }

  function emitString(string memory s) public {
    emit WithStringArg(s);
  }

  function emitIndexedString(string memory s) public {
    emit WithIndexedStringArg(s);
  }

  function emitBytes(bytes memory b) public {
    emit WithBytesArg(b);
  }

  function emitIndexedBytes(bytes memory b) public {
    emit WithIndexedBytesArg(b);
  }

  function emitBytes32(bytes32 b) public {
    emit WithBytes32Arg(b);
  }

  function emitIndexedBytes32(bytes32 b) public {
    emit WithIndexedBytes32Arg(b);
  }

  function emitUintAndString(uint u, string memory s) public {
    emit WithStringArg(s);
    emit WithUintArg(u);
  }

  function emitTwoUintsAndTwoStrings(uint u, uint v, string memory s, string memory t) public {
    emit WithTwoUintArgs(u, v);
    emit WithTwoStringArgs(s, t);
  }

  function emitStruct(uint u, uint v) public {
    emit WithStructArg(Struct(u, v));
  }

  function emitUintArray(uint u, uint v) public {
    emit WithUintArray([u, v]);
  }

  function emitBytes32Array(bytes32 b, bytes32 c) public {
    emit WithBytes32Array([b, c]);
  }

  function emitNestedUintFromSameContract(uint u) public {
    emitUint(u);
  }

  function emitNestedUintFromAnotherContract(uint u) public {
    anotherContract.emitUint(u);
  }
}

contract AnotherContract {
  event WithUintArg(uint u);

  function emitUint(uint u) public {
    emit WithUintArg(u);
  }
}

contracts/Matchers.sol.content:
// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

contract Matchers {
  uint x;

  event SomeEvent();

  AnotherMatchersContract anotherContract;

  struct Pair { uint a; uint b; }

  error SomeCustomError();
  error AnotherCustomError();
  error CustomErrorWithInt(int);
  error CustomErrorWithUint(uint nameToForceEthersToUseAnArrayLikeWithNamedProperties);
  error CustomErrorWithUintAndString(uint, string);
  error CustomErrorWithPair(Pair);

  constructor () {
    anotherContract = new AnotherMatchersContract();
  }

  function succeeds() public {
    x++; // just to avoid compiler warnings
  }

  function succeedsView() public view returns (uint) {
    return x;
  }

  function revertsWith(string memory reason) public {
    x++;
    require(false, reason);
  }

  function revertsWithView(string memory reason) public pure {
    require(false, reason);
  }

  function revertsWithoutReason() public {
    x++;
    require(false);
  }

  function revertsWithoutReasonView() public pure {
    require(false);
  }

  function panicAssert() public {
    x++;
    assert(false);
  }

  function panicAssertView() public pure {
    assert(false);
  }

  function revertWithSomeCustomError() public {
    x++;
    revert SomeCustomError();
  }

  function revertWithSomeCustomErrorView() public pure {
    revert SomeCustomError();
  }

  function revertWithAnotherCustomError() public {
    x++;
    revert AnotherCustomError();
  }

  function revertWithAnotherCustomErrorView() public pure {
    revert AnotherCustomError();
  }

  function revertWithAnotherContractCustomError() public {
    x++;
    anotherContract.revertWithYetAnotherCustomError();
  }

  function revertWithAnotherContractCustomErrorView() public view {
    anotherContract.revertWithYetAnotherCustomErrorView();
  }

  function revertWithCustomErrorWithUint(uint n) public {
    x++;
    revert CustomErrorWithUint(n);
  }

  function revertWithCustomErrorWithUintView(uint n) public pure {
    revert CustomErrorWithUint(n);
  }

  function revertWithCustomErrorWithInt(int i) public {
    x++;
    revert CustomErrorWithInt(i);
  }

  function revertWithCustomErrorWithIntView(int i) public pure {
    revert CustomErrorWithInt(i);
  }

  function revertWithCustomErrorWithUintAndString(uint n, string memory s) public {
    x++;
    revert CustomErrorWithUintAndString(n, s);
  }

  function revertWithCustomErrorWithUintAndStringView(uint n, string memory s) public pure {
    revert CustomErrorWithUintAndString(n, s);
  }

  function revertWithCustomErrorWithPair(uint a, uint b) public {
    x++;
    revert CustomErrorWithPair(Pair(a, b));
  }

  function revertWithCustomErrorWithPairView(uint a, uint b) public pure {
    revert CustomErrorWithPair(Pair(a, b));
  }
}

contract AnotherMatchersContract {
  uint x;

  error YetAnotherCustomError();

  function revertWithYetAnotherCustomError() public {
    x++;
    revert YetAnotherCustomError();
  }

  function revertWithYetAnotherCustomErrorView() public pure {
    revert YetAnotherCustomError();
  }
}

contracts/Token.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract TokenWithoutNameNorSymbol {
  uint public decimals = 1;

  uint public totalSupply;
  mapping(address => uint) public balanceOf;
  mapping(address => mapping(address => uint)) allowances;

  constructor () {
    totalSupply = 1_000_000_000;
    balanceOf[msg.sender] = totalSupply;
  }

  function transfer(address to, uint value) public returns (bool) {
    require(value > 0, "Transferred value is zero");

    balanceOf[msg.sender] -= value;
    balanceOf[to] += value;

    return true;
  }

  function allowance(address owner, address spender) public view returns (uint256 remaining) {
    return allowances[owner][spender];
  }

  function approve(address spender, uint256 value) public returns (bool success) {
    allowances[msg.sender][spender] = value;
    return true;
  }

  function transferFrom(address from, address to, uint256 value) public returns (bool) {
    require(allowance(from, msg.sender) >= value, "Insufficient allowance");

    allowances[from][msg.sender] -= value;
    balanceOf[from] -= value;
    balanceOf[to] += value;

    return true;
  }
}

contract TokenWithOnlyName is TokenWithoutNameNorSymbol {
  string public name = "MockToken";
}

contract MockToken is TokenWithoutNameNorSymbol {
  string public name = "MockToken";
  string public symbol = "MCK";
}

contract NotAToken {}

contracts/TwoUserMultisig.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@matterlabs/zksync-contracts/l2/system-contracts/interfaces/IAccount.sol";
import "@matterlabs/zksync-contracts/l2/system-contracts/libraries/TransactionHelper.sol";

import "@openzeppelin/contracts/interfaces/IERC1271.sol";

// Used for signature validation
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

// Access zkSync system contracts, in this case for nonce validation vs NONCE_HOLDER_SYSTEM_CONTRACT
import "@matterlabs/zksync-contracts/l2/system-contracts/Constants.sol";
// to call non-view method of system contracts
import "@matterlabs/zksync-contracts/l2/system-contracts/libraries/SystemContractsCaller.sol";

contract TwoUserMultisig is IAccount, IERC1271 {
    // to get transaction hash
    using TransactionHelper for Transaction;

    // state variables for account owners
    address public owner1;
    address public owner2;

    bytes4 constant EIP1271_SUCCESS_RETURN_VALUE = 0x1626ba7e;

    modifier onlyBootloader() {
        require(
            msg.sender == BOOTLOADER_FORMAL_ADDRESS,
            "Only bootloader can call this method"
        );
        // Continure execution if called from the bootloader.
        _;
    }

    constructor(address _owner1, address _owner2) {
        owner1 = _owner1;
        owner2 = _owner2;
    }

    function validateTransaction(
        bytes32,
        bytes32 _suggestedSignedHash,
        Transaction calldata _transaction
    ) external payable override onlyBootloader returns (bytes4 magic) {
        magic = _validateTransaction(_suggestedSignedHash, _transaction);
    }

    function _validateTransaction(
        bytes32 _suggestedSignedHash,
        Transaction calldata _transaction
    ) internal returns (bytes4 magic) {
        // Incrementing the nonce of the account.
        // Note, that reserved[0] by convention is currently equal to the nonce passed in the transaction
        SystemContractsCaller.systemCallWithPropagatedRevert(
            uint32(gasleft()),
            address(NONCE_HOLDER_SYSTEM_CONTRACT),
            0,
            abi.encodeCall(INonceHolder.incrementMinNonceIfEquals, (_transaction.nonce))
        );

        bytes32 txHash;
        // While the suggested signed hash is usually provided, it is generally
        // not recommended to rely on it to be present, since in the future
        // there may be tx types with no suggested signed hash.
        if (_suggestedSignedHash == bytes32(0)) {
            txHash = _transaction.encodeHash();
        } else {
            txHash = _suggestedSignedHash;
        }

        // The fact there is are enough balance for the account
        // should be checked explicitly to prevent user paying for fee for a
        // transaction that wouldn't be included on Ethereum.
        uint256 totalRequiredBalance = _transaction.totalRequiredBalance();
        require(totalRequiredBalance <= address(this).balance, "Not enough balance for fee + value");

        if (isValidSignature(txHash, _transaction.signature) == EIP1271_SUCCESS_RETURN_VALUE) {
            magic = ACCOUNT_VALIDATION_SUCCESS_MAGIC;
        }
    }

    function executeTransaction(
        bytes32,
        bytes32,
        Transaction calldata _transaction
    ) external payable override onlyBootloader {
        _executeTransaction(_transaction);
    }

    function _executeTransaction(Transaction calldata _transaction) internal {
        address to = address(uint160(_transaction.to));
        uint128 value = Utils.safeCastToU128(_transaction.value);
        bytes memory data = _transaction.data;

        if (to == address(DEPLOYER_SYSTEM_CONTRACT)) {
            uint32 gas = Utils.safeCastToU32(gasleft());

            // Note, that the deployer contract can only be called
            // with a "systemCall" flag.
            SystemContractsCaller.systemCallWithPropagatedRevert(gas, to, value, data);
        } else {
            bool success;
            assembly {
                success := call(gas(), to, value, add(data, 0x20), mload(data), 0, 0)
            }
            require(success);
        }
    }

    function executeTransactionFromOutside(Transaction calldata _transaction)
        external
        payable
    {
        _validateTransaction(bytes32(0), _transaction);
        _executeTransaction(_transaction);
    }

    function isValidSignature(bytes32 _hash, bytes memory _signature)
        public
        view
        override
        returns (bytes4 magic)
    {
        magic = EIP1271_SUCCESS_RETURN_VALUE;

        if (_signature.length != 130) {
            // Signature is invalid anyway, but we need to proceed with the signature verification as usual
            // in order for the fee estimation to work correctly
            _signature = new bytes(130);

            // Making sure that the signatures look like a valid ECDSA signature and are not rejected rightaway
            // while skipping the main verification process.
            _signature[64] = bytes1(uint8(27));
            _signature[129] = bytes1(uint8(27));
        }

        (bytes memory signature1, bytes memory signature2) = extractECDSASignature(_signature);

        if(!checkValidECDSASignatureFormat(signature1) || !checkValidECDSASignatureFormat(signature2)) {
            magic = bytes4(0);
        }

        (address recoveredAddr1, ECDSA.RecoverError error) = ECDSA.tryRecover(_hash, signature1);
        if (error != ECDSA.RecoverError.NoError)  {
            magic = bytes4(0);
            return magic;
        }

        (address recoveredAddr2, ECDSA.RecoverError error2) = ECDSA.tryRecover(_hash, signature2);
        if (error2 != ECDSA.RecoverError.NoError)  {
            magic = bytes4(0);
            return magic;
        }

        // Note, that we should abstain from using the require here in order to allow for fee estimation to work
        if(recoveredAddr1 != owner1 || recoveredAddr2 != owner2) {
            magic = bytes4(0);
        }
    }

    // This function verifies that the ECDSA signature is both in correct format and non-malleable
    function checkValidECDSASignatureFormat(bytes memory _signature) internal pure returns (bool) {
        if(_signature.length != 65) {
            return false;
        }

        uint8 v;
		bytes32 r;
		bytes32 s;
		// Signature loading code
		// we jump 32 (0x20) as the first slot of bytes contains the length
		// we jump 65 (0x41) per signature
		// for v we load 32 bytes ending with v (the first 31 come from s) then apply a mask
		assembly {
			r := mload(add(_signature, 0x20))
			s := mload(add(_signature, 0x40))
			v := and(mload(add(_signature, 0x41)), 0xff)
		}
		if(v != 27 && v != 28) {
            return false;
        }

		// EIP-2 still allows signature malleability for ecrecover(). Remove this possibility and make the signature
        // unique. Appendix F in the Ethereum Yellow paper (https://ethereum.github.io/yellowpaper/paper.pdf), defines
        // the valid range for s in (301): 0 < s < secp256k1n ÷ 2 + 1, and for v in (302): v ∈ {27, 28}. Most
        // signatures from current libraries generate a unique signature with an s-value in the lower half order.
        //
        // If your library generates malleable signatures, such as s-values in the upper range, calculate a new s-value
        // with 0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFEBAAEDCE6AF48A03BBFD25E8CD0364141 - s1 and flip v from 27 to 28 or
        // vice versa. If your library also generates signatures with 0/1 for v instead 27/28, add 27 to v to accept
        // these malleable signatures as well.
        if(uint256(s) > 0x7FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF5D576E7357A4501DDFE92F46681B20A0) {
            return false;
        }

        return true;
    }

    function extractECDSASignature(bytes memory _fullSignature) internal pure returns (bytes memory signature1, bytes memory signature2) {
        require(_fullSignature.length == 130, "Invalid length");

        signature1 = new bytes(65);
        signature2 = new bytes(65);

        // Copying the first signature. Note, that we need an offset of 0x20 
        // since it is where the length of the `_fullSignature` is stored
        assembly {
            let r := mload(add(_fullSignature, 0x20))
			let s := mload(add(_fullSignature, 0x40))
			let v := and(mload(add(_fullSignature, 0x41)), 0xff)

            mstore(add(signature1, 0x20), r)
            mstore(add(signature1, 0x40), s)
            mstore8(add(signature1, 0x60), v)
        }

        // Copying the second signature.
        assembly {
            let r := mload(add(_fullSignature, 0x61))
            let s := mload(add(_fullSignature, 0x81))
            let v := and(mload(add(_fullSignature, 0x82)), 0xff)

            mstore(add(signature2, 0x20), r)
            mstore(add(signature2, 0x40), s)
            mstore8(add(signature2, 0x60), v)
        }
    }

    function payForTransaction(
        bytes32,
        bytes32,
        Transaction calldata _transaction
    ) external payable override onlyBootloader {
        bool success = _transaction.payToTheBootloader();
        require(success, "Failed to pay the fee to the operator");
    }

    function prepareForPaymaster(
        bytes32, // _txHash
        bytes32, // _suggestedSignedHash
        Transaction calldata _transaction
    ) external payable override onlyBootloader {
        _transaction.processPaymasterInput();
    }

    fallback() external {
        // fallback of default account shouldn't be called by bootloader under no circumstances
        assert(msg.sender != BOOTLOADER_FORMAL_ADDRESS);

        // If the contract is called directly, behave like an EOA
    }

    receive() external payable {
        // If the contract is called directly, behave like an EOA.
        // Note, that is okay if the bootloader sends funds with no calldata as it may be used for refunds/operator payments
    }
}

